const express = require("express");
const logger = require("morgan");
const cors = require("cors");
const { startApp } = require("./databaseSetup");
const ourLogger = require("./logger");

const testRouter = require("./components/test");
const usersRouter = require("./routes/users");
const categoryRouter = require("./routes/categories");
const eventsRouter = require("./routes/events");
const accountRouter = require("./routes/account");
const adminContactsRoutes = require("./routes/contactAdmin");
const unitOfMeasuringsRoutes = require("./routes/unitofmeasurings.js");
const categoryOfMeasuringsRoutes = require("./routes/categoryOfMeasuring");

const app = express();

const formatsLogger = app.get("env") === "development" ? "dev" : "short";

app.use(logger(formatsLogger));
app.use(cors());
app.use(express.json());
app.use(ourLogger());

app.use("/api/UnitOfMeasuring", unitOfMeasuringsRoutes);
app.use("/api/test", testRouter);
app.use("/api/users", usersRouter);
app.use("/api/Category", categoryRouter);
app.use("/api/events", eventsRouter);
app.use("/api/account", accountRouter);
app.use("/api/ContactAdmin", adminContactsRoutes);
app.use("/api/CategoryOfMeasuring", categoryOfMeasuringsRoutes);

app.use((_, res) => {
  res.status(404).json({ message: "Not found" });
});

app.use((err, _, res, __) => {
  const { status = 500, message = "Internal Server Error" } = err;

  res.status(status).json(message);
});

startApp(app);

/* eslint-disable no-undef */
const fs = require("fs");
const path = require("path");
const { performance } = require("perf_hooks");

const date = new Date();

const fileName = `${date.getFullYear()}-${
  date.getMonth() + 1
}-${date.getDate()}-log`;

const pathLogDir = path.join(process.env.HOME, "EventsExpressNode-logs");
const pathLogFile = path.join(pathLogDir, fileName);

const isAccessible = (path) => {
  return fs.accessSync(path);
};

const logger = () => {
  try {
    isAccessible(pathLogDir);
  } catch (error) {
    fs.mkdirSync(pathLogDir);
  }

  try {
    isAccessible(pathLogFile);
  } catch (error) {
    fs.writeFileSync(pathLogFile, "");
  }

  return (req, _, next) => {
    try {
      const start = performance.now();
      next();

      const perf = performance.now() - start;
      const newLog = `\n${req.method} ${req.originalUrl} ${perf}`;
      fs.appendFileSync(pathLogFile, newLog);
    } catch (error) {
      console.log(error);
    }
  };
};

module.exports = logger;

const container = (ctrl) => async (req, res, next) => {
  try {
    await ctrl(req, res);
  } catch (error) {
	res.json({ message: error.message });
  }
};

module.exports = container;

